import React from 'react';
import './App.scss';
import MenuContainer from './components/MenuContainer';
import Popup from './PopUp';

export default class App extends React.Component {
  state = {
    showPopup: false,
  };

  componentDidMount(){
    document.addEventListener('popUp', ()=> this.setState({showPopup: true}));
  }
  render() {
    return (
      <div className='App'>
        <MenuContainer />
        {this.state.showPopup ? 
          <Popup
            text='Close Me'

          />
          : null
        }
      </div>
    );
  }
}
